			val expectedStateRoot = "d7f8974fb5ac78d9ac099b9ad5018bedc2ce0a72dad1827a1709da30580f0544"

			genesis.hash.toHexString mustEqual expectedHash
			genesis.stateRoot.toHexString mustEqual expectedStateRoot
